pragma solidity 0.8.7;
pragma abicoder v2;

/**
 * @author Eman Herawy, StartFi Team
 *@title  MarketPlace Bid
 * [ desc ] : contract handles all the bid related functions for marketplace
 */
contract MarketPlaceBid {
    // using Address for address;
    // using EnumerableSet for EnumerableSet.UintSet;
    // using EnumerableSet for EnumerableSet.AddressSet;
    struct Bid {
        bytes32 bidId;
        address nFTContract;
        uint256 tokenId;
        uint256 bidPrice;
        bool isPurchased;
    }
    struct WinningBid {
        bytes32 bidId;
        address bidder;
    }
    // lisingId to bid key to bid details
    mapping(bytes32 => mapping(address => Bid)) internal listingBids;
    // track the bid latest bid id
    mapping(bytes32 => WinningBid) internal bidToListing;

    /******************************************* read state functions go here ********************************************************* */
    /**
     *
     * @dev   called by dapp or any contract to get info about a winner bid
     * @param listingId listing id
     * @return bidId bid id
     * @return bidder bidder address
     * @return nFTContract  nft contract address
     * @return tokenId nft token id
     * @return bidPrice bid price
     * @return isPurchased true if purchased
     */
    function winnerBid(bytes32 listingId)
        external
        view
        returns (
            bytes32 bidId,
            address bidder,
            address nFTContract,
            uint256 tokenId,
            uint256 bidPrice,
            bool isPurchased
        )
    {
        bidId = bidToListing[listingId].bidId;
        bidder = bidToListing[listingId].bidder;
        nFTContract = listingBids[listingId][bidder].nFTContract;
        tokenId = listingBids[listingId][bidder].tokenId;
        bidPrice = listingBids[listingId][bidder].bidPrice;
        isPurchased = listingBids[listingId][bidder].isPurchased;
    }

    /**
     *
     * @dev   called by dapp or any contract to get info about a given bidder in a listing
     * @param listingId listing id
     * @param bidder bidder address
     * @return bidId bid id
     * @return nFTContract  nft contract address
     * @return tokenId nft token id
     * @return bidPrice bid price
     */
    function getAuctionBidDetails(bytes32 listingId, address bidder)
        external
        view
        returns (
            bytes32 bidId,
            address nFTContract,
            uint256 tokenId,
            uint256 bidPrice,
            bool isPurchased
        )
    {
        bidId = listingBids[listingId][bidder].bidId;
        nFTContract = listingBids[listingId][bidder].nFTContract;
        tokenId = listingBids[listingId][bidder].tokenId;
        bidPrice = listingBids[listingId][bidder].bidPrice;
        isPurchased = listingBids[listingId][bidder].isPurchased;
    }

    /******************************************* change state functions go here ********************************************************* */

    /**
     * @notice  all conditions and checks are made prior to this function
     * @dev  add new bid , update the latest bidder to be his bid
     * @param bidId bid id
     * @param listingId listing id
     * @param tokenAddress nft contract address
     * @param bidder bidder address
     * @param tokenId token id
     * @param bidPrice price
     * @return true if it's done
     */
    function _bid(
        bytes32 bidId,
        bytes32 listingId,
        address tokenAddress,
        address bidder,
        uint256 tokenId,
        uint256 bidPrice
    ) internal returns (bool) {
        // where bid winner is the last bidder updated
        bidToListing[listingId] = WinningBid(bidId, bidder);
        listingBids[listingId][bidder] = Bid(bidId, tokenAddress, tokenId, bidPrice, false);
        return true;
    }
}
","keccak256":"0x879991b2c29021eddde3fa9d220b525daebf8bfcbbc3cdde63928fcfa8290fac","license":"AGPL-3.0-or-later"},"contracts/MarketPlaceListing.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0-or-later

pragma solidity 0.8.7;
pragma abicoder v2;

/**
 * @author Eman Herawy, StartFi Team
 *@title  MarketPlace Listing
 * [ desc ] : contract handle all item list in marketplace related function
 */
contract MarketPlaceListing {
    // all fees are in perentage

    // delist after 6 month
    uint256 public delistAfter = 6 * 30 days;

    constructor() {}

    // using Address for address;
    // using EnumerableSet for EnumerableSet.UintSet;
    // using EnumerableSet for EnumerableSet.AddressSet;
    struct Listing {
        address nFTContract;
        uint256 tokenId;
        uint256 listingPrice;
        uint256 endPrice;
        address seller;
        address buyer;
        bool bedEnabeled;
        bool sellForEnabled;
        // only if bed and sell for enabled
        uint256 releaseTime;
        uint256 qualifyAmount;
        uint256 sellFor;
        ListingStatus status;
    }
    enum ListingStatus {
        Sold,
        OnMarket,
        onAuction,
        Canceled
    }
    // listing key  to lisitng details
    mapping(bytes32 => Listing) internal _tokenListings;

    // track the Listinger total amount of Listings
    // mapping (address=>uint256) private userTotalListings;
    /******************************************* read state functions go here ********************************************************* */
    /**
    * 
      * @dev   called by dapp or any contract to get info about a gevin listing    
      * @param listingId listing id      

      * @return tokenAddress  nft contract address
      * @return tokenId NFT token Id 
      * @return listingPrice initial price or minimum price that the seller can accept
      * @return endPrice purchase price
      * @return seller  nft seller address
      * @return buyer  nft buyer address
      * @return bedEnabeled true if auction enabled  
      * @return sellForEnabled true if auction enable direct selling
      * @return releaseTime  when auction ends
      * @return qualifyAmount  amount of token locked as qualify for any bidder wants bid 
      * @return sellFor if sell for enabled for auction, this should be more than zero
      * @return status in number {Sold,OnMarket, onAuction,Canceled}
     */
    function getListingDetails(bytes32 listingId)
        external
        view
        returns (
            address tokenAddress,
            uint256 tokenId,
            uint256 listingPrice,
            uint256 endPrice,
            address seller,
            address buyer,
            bool bedEnabeled,
            bool sellForEnabled,
            uint256 releaseTime,
            uint256 qualifyAmount,
            uint256 sellFor,
            uint256 status
        )
    {
        tokenAddress = _tokenListings[listingId].nFTContract;
        tokenId = _tokenListings[listingId].tokenId;
        listingPrice = _tokenListings[listingId].listingPrice;
        endPrice = _tokenListings[listingId].endPrice;
        seller = _tokenListings[listingId].seller;
        buyer = _tokenListings[listingId].buyer;
        bedEnabeled = _tokenListings[listingId].bedEnabeled;
        sellForEnabled = _tokenListings[listingId].sellForEnabled;
        releaseTime = _tokenListings[listingId].releaseTime;
        qualifyAmount = _tokenListings[listingId].qualifyAmount;
        sellFor = _tokenListings[listingId].sellFor;
        status = uint256(_tokenListings[listingId].status);
    }

    // list

    /**
     * @notice  all conditions and checks are made prior to this function
     * @dev  add new item for sale in marketplace
     * @param listId listing id
     * @param tokenAddress nft contract address
     * @param seller seller address
     * @param tokenId token id
     * @param listingPrice min price
     * @param releaseTime  time to delist for free
     * @return true if it's done
     */
    function _listOnMarketPlace(
        bytes32 listId,
        address tokenAddress,
        address seller,
        uint256 tokenId,
        uint256 listingPrice,
        uint256 releaseTime
    ) internal returns (bool) {
        _tokenListings[listId] = Listing(
            tokenAddress,
            tokenId,
            listingPrice,
            0,
            seller,
            address(0),
            false,
            false,
            releaseTime,
            0,
            0,
            ListingStatus.OnMarket
        );
        return true;
    }

    /**
     * @notice  all conditions and checks are made prior to this function
     * @dev  add new auction
     * @param listId listing id
     * @param tokenAddress nft contract address
     * @param seller seller address
     * @param tokenId token id
     * @param listingPrice min price
     * @param sellForEnabled true if auction enable direct selling
     * @param sellFor  price  to sell with if sellForEnabled=true
     * @param releaseTime  when auction ends
     * @param qualifyAmount  amount of token locked as qualify for any bidder wants bid
     * @return true if it's done
     */
    function _creatAuction(
        bytes32 listId,
        address tokenAddress,
        address seller,
        uint256 tokenId,
        uint256 listingPrice,
        bool sellForEnabled,
        uint256 sellFor,
        uint256 releaseTime,
        uint256 qualifyAmount
    ) internal returns (bool) {
        _tokenListings[listId] = Listing(
            tokenAddress,
            tokenId,
            listingPrice,
            0,
            seller,
            address(0),
            true,
            sellForEnabled,
            releaseTime,
            qualifyAmount,
            sellFor,
            ListingStatus.onAuction
        );
        return true;
    }

    function _finalizeListing(
        bytes32 listId,
        address buyer,
        ListingStatus status
    ) internal {
        _tokenListings[listId].status = status;
        if (buyer != address(0)) {
            _tokenListings[listId].buyer = buyer;
        }
    }

    /**
     *  @notice  all conditions and checks are made prior to this function
     * @dev  delist an item by mark status as canceled
     * @param listingId listing id
     *
     */
    function _deList(bytes32 listingId) internal {
        _tokenListings[listingId].status = ListingStatus.Canceled;
    }

    /**
     *  @notice  all conditions and checks are made prior to this function
     * @dev  change the duration of which user can delist thier nfts for free after it
     * @param duration in seconds , eg 30 days in desconds
     *
     */
    function _changeDelistAfter(uint256 duration) internal {
        delistAfter = duration;
    }
}
","keccak256":"0xacc6a150bf6fcc3034d239f621d1911dc64eee81805d22cfd0e79d9f393f5c44","license":"AGPL-3.0-or-later"},"contracts/StartFiMarketPlace.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0-or-later

pragma solidity 0.8.7;
import './StartfiMarketPlaceFinance.sol';
import './MarketPlaceListing.sol';
import './MarketPlaceBid.sol';
import '@openzeppelin/contracts/security/Pausable.sol';
import '@openzeppelin/contracts/access/Ownable.sol';

/**
 * @author Eman Herawy, StartFi Team
 *@title StartFi MarketPlace
 *desc  marketplace with all functions for item selling by either ceating auction or selling with fixed prices, the contract auto transfer orginal NFT issuer's shares
 *
 */
contract StartFiMarketPlace is Ownable, Pausable, MarketPlaceListing, MarketPlaceBid, StartfiMarketPlaceFinance {
    /******************************************* decalrations go here ********************************************************* */
    // TODO: to be updated ( using value or percentage?? develop function to ready and update the value)
    uint256 minQualifyAmount = 10;

    // events when auction created auction bid auction cancled auction fullfiled item listed , item purchesed , itme delisted , item delist with deduct , item  disputed , user free reserved ,
    ///
    event ListOnMarketplace(
        bytes32 listId,
        address nFTContract,
        address buyer,
        uint256 tokenId,
        uint256 listingPrice,
        uint256 releaseTime,
        uint256 qualifyAmount,
        uint256 timestamp
    );
    event DeListOffMarketplace(
        bytes32 listId,
        address nFTContract,
        address owner,
        uint256 tokenId,
        uint256 fineFees,
        uint256 remaining,
        uint256 releaseTime,
        uint256 timestamp
    );

    event CreateAuction(
        bytes32 listId,
        address nFTContract,
        address seller,
        uint256 tokenId,
        uint256 listingPrice,
        bool sellForEnabled,
        uint256 sellFor,
        uint256 releaseTime,
        uint256 qualifyAmount,
        uint256 timestamp
    );

    event BidOnAuction(
        bytes32 bidId,
        bytes32 listingId,
        address tokenAddress,
        address bidder,
        uint256 tokenId,
        uint256 bidPrice,
        uint256 timestamp
    );

    event FullfillBid(
        bytes32 bidId,
        bytes32 listingId,
        address tokenAddress,
        address bidder,
        uint256 tokenId,
        uint256 bidPrice,
        address issuer,
        uint256 royaltyAmount,
        uint256 fees,
        uint256 netPrice,
        uint256 timestamp
    );

    event DisputeAuction(
        bytes32 bidId,
        bytes32 listingId,
        address tokenAddress,
        address bidder,
        uint256 tokenId,
        address seller,
        uint256 qualifyAmount,
        uint256 remaining,
        uint256 finefees,
        uint256 timestamp
    );

    event BuyNow(
        bytes32 listId,
        address nFTContract,
        address buyer,
        uint256 tokenId,
        uint256 sellingPrice,
        address seller,
        bool isAucton,
        address issuer,
        uint256 royaltyAmount,
        uint256 fees,
        uint256 netPrice,
        uint256 timestamp
    );
    event UserReservesFree(address user, uint256 lastReserves, uint256 newReserves, uint256 timestamp);

    /******************************************* constructor goes here ********************************************************* */

    constructor(
        string memory _marketPlaceName,
        address _paymentContract,
        address _stakeContract,
        address _reputationContract
    ) StartfiMarketPlaceFinance(_marketPlaceName, _paymentContract, _reputationContract) {
        stakeContract = _stakeContract;
    }

    /******************************************* modifiers go here ********************************************************* */

    modifier isOpenAuction(bytes32 listingId) {
        require(
            _tokenListings[listingId].releaseTime > block.timestamp &&
                _tokenListings[listingId].status == ListingStatus.onAuction,
            'Auction is ended'
        );
        _;
    }
    modifier canFullfillBid(bytes32 listingId) {
        require(
            _tokenListings[listingId].releaseTime < block.timestamp &&
                _tokenListings[listingId].status == ListingStatus.onAuction,
            'Auction is ended'
        );
        _;
    }
    modifier isOpenForSale(bytes32 listingId) {
        require(_tokenListings[listingId].status == ListingStatus.OnMarket, 'Item is not for sale');
        _;
    }
    modifier isNotZero(uint256 val) {
        require(val > 0, 'Zero Value is not allowed');
        _;
    }

    /******************************************* read state functions go here ********************************************************* */

    /******************************************* state functions go here ********************************************************* */

    // list
    /**
     * @dev  called by dapps to list new item
     * @param nFTContract nft contract address
     * @param tokenId token id
     * @param listingPrice min price
     * @return listId listing id
     */
    function listOnMarketplace(
        address nFTContract,
        uint256 tokenId,
        uint256 listingPrice
    ) external isNotZero(listingPrice) returns (bytes32 listId) {
        uint256 releaseTime = _calcSum(block.timestamp, delistAfter);
        listId = keccak256(abi.encodePacked(nFTContract, tokenId, _msgSender(), releaseTime));
        // calc qualified ammount
        uint256 listQualifyAmount = _getListingQualAmount(listingPrice);

        // check that sender is qualified
        require(
            _getStakeAllowance(
                _msgSender() /*, 0*/
            ) >= listQualifyAmount,
            'Not enough reserves'
        );
        require(_isTokenApproved(nFTContract, tokenId), 'Marketplace is not allowed to transfer your token');

        // transfer token to contract
        require(
            _safeNFTTransfer(nFTContract, tokenId, _msgSender(), address(this)),
            "NFT token couldn't be transfered"
        );

        // update reserved
        _updateUserReserves(_msgSender(), listQualifyAmount, true);
        bytes32[] storage listings = userListing[_msgSender()];
        listings.push(listId);
        userListing[_msgSender()] = listings;
        // list
        require(
            _listOnMarketPlace(listId, nFTContract, _msgSender(), tokenId, listingPrice, releaseTime),
            "Couldn't list the item"
        );
        emit ListOnMarketplace(
            listId,
            nFTContract,
            _msgSender(),
            tokenId,
            listingPrice,
            releaseTime,
            listQualifyAmount,
            block.timestamp
        );
    }

    // create auction
    /**
     * @dev  called by dapps to create  new auction
     * @param nFTContract nft contract address
     * @param tokenId token id
     * @param listingPrice min price
     * @param qualifyAmount  amount of token locked as qualify for any bidder wants bid
     * @param sellForEnabled true if auction enable direct selling
     * @param sellFor  price  to sell with if sellForEnabled=true
     * @param duration  when auction ends
     * @return listId listing id
     */
    function createAuction(
        address nFTContract,
        uint256 tokenId,
        uint256 listingPrice,
        uint256 qualifyAmount,
        bool sellForEnabled,
        uint256 sellFor,
        uint256 duration
    ) external isNotZero(listingPrice) returns (bytes32 listId) {
        require(duration > 12 hours, 'Auction should be live for more than 12 hours');
        require(qualifyAmount >= minQualifyAmount, 'Invalid Auction qualify Amount');

        uint256 releaseTime = _calcSum(block.timestamp, duration);
        listId = keccak256(abi.encodePacked(nFTContract, tokenId, _msgSender(), releaseTime));
        if (sellForEnabled) {
            require(sellFor > 0, 'Zero price is not allowed');
        }
        // check that sender is qualified
        require(_isTokenApproved(nFTContract, tokenId), 'Marketplace is not allowed to transfer your token');

        // transfer token to contract
        require(
            _safeNFTTransfer(nFTContract, tokenId, _msgSender(), address(this)),
            "NFT token couldn't be transfered"
        );

        // update reserved
        // create auction

        require(
            _creatAuction(
                listId,
                nFTContract,
                _msgSender(),
                tokenId,
                listingPrice,
                sellForEnabled,
                sellFor,
                releaseTime,
                qualifyAmount
            ),
            "Couldn't list the item"
        );
        emit CreateAuction(
            listId,
            nFTContract,
            _msgSender(),
            tokenId,
            listingPrice,
            sellForEnabled,
            sellFor,
            releaseTime,
            qualifyAmount,
            block.timestamp
        );
    }

    /**
     * @dev called by dapps to bid on an auction
     *
     * @param listingId listing id
     * @param bidPrice price
     * @return bidId bid id
     */
    function bid(bytes32 listingId, uint256 bidPrice) external isOpenAuction(listingId) returns (bytes32 bidId) {
        address tokenAddress = _tokenListings[listingId].nFTContract;
        uint256 tokenId = _tokenListings[listingId].tokenId;
        bidId = keccak256(abi.encodePacked(listingId, tokenAddress, _msgSender(), tokenId));
        // bid should be more than than the mini and more than the last bid
        address lastbidder = bidToListing[listingId].bidder;
        uint256 qualifyAmount = _tokenListings[listingId].qualifyAmount;
        if (lastbidder == address(0)) {
            require(
                bidPrice >= _tokenListings[listingId].listingPrice,
                'bid price must be more than or equal the minimum price'
            );
        } else {
            require(bidPrice > listingBids[listingId][lastbidder].bidPrice, 'bid price must be more than the last bid');
        }
        // if this is the bidder first bid, the price will be 0
        uint256 prevAmount = listingBids[listingId][_msgSender()].bidPrice;
        if (prevAmount == 0) {
            // check that he has reserved
            require(
                _getStakeAllowance(
                    _msgSender() /*, 0*/
                ) >= qualifyAmount,
                'Not enough reserves'
            );
            bytes32[] storage listings = userListing[_msgSender()];
            listings.push(listingId);
            userListing[_msgSender()] = listings;
            // update user reserves
            // reserve Zero couldn't be at any case
            require(_updateUserReserves(_msgSender(), qualifyAmount, true) > 0, 'Reserve Zero is not allowed');
        }

        // bid
        require(_bid(bidId, listingId, tokenAddress, _msgSender(), tokenId, bidPrice), "Couldn't Bid");
        emit BidOnAuction(bidId, listingId, tokenAddress, _msgSender(), tokenId, bidPrice, block.timestamp);

        // if bid time is less than 15 min, increase by 15 min
        // retuen bid id
    }

    /**
     * @dev called by bidder through dapps when bidder win an auction and wants to pay to get the NFT
     *
     * @param listingId listing id
     * @return _NFTContract nft contract address
     * @return tokenId token id
     */
    function fullfillBid(bytes32 listingId)
        external
        canFullfillBid(listingId)
        returns (address _NFTContract, uint256 tokenId)
    {
        address winnerBidder = bidToListing[listingId].bidder;
        address seller = _tokenListings[listingId].seller;
        _NFTContract = _tokenListings[listingId].nFTContract;
        tokenId = _tokenListings[listingId].tokenId;
        require(winnerBidder == _msgSender(), 'Caller is not the winner');
        // if it's new, the price will be 0
        uint256 bidPrice = listingBids[listingId][winnerBidder].bidPrice;
        // check that contract is allowed to transfer tokens
        require(
            _getAllowance(winnerBidder) >= bidPrice,
            'Marketplace is not allowed to withdraw the required amount of tokens'
        );
        // transfer price

        (address issuer, uint256 royaltyAmount, uint256 fees, uint256 netPrice) = _getListingFinancialInfo(
            _NFTContract,
            tokenId,
            bidPrice
        );

        require(_safeTokenTransferFrom(winnerBidder, owner(), fees), "Couldn't transfer token as fees");
        if (issuer != address(0)) {
            require(_safeTokenTransferFrom(winnerBidder, issuer, royaltyAmount), "Couldn't transfer token to issuer");
        }

        // token value could be zero ater taking the roylty share ??? need to ask?
        require(_safeTokenTransferFrom(winnerBidder, seller, netPrice), "Couldn't transfer token to buyer");
        // trnasfer token
        require(
            _safeNFTTransfer(_NFTContract, tokenId, address(this), winnerBidder),
            "NFT token couldn't be transfered"
        );
        // update user reserves
        // reserve nigative couldn't be at any case
        require(
            _updateUserReserves(winnerBidder, _tokenListings[listingId].qualifyAmount, false) >= 0,
            'negative reserve is not allowed'
        );
        listingBids[listingId][_msgSender()].isPurchased = true;
        // TODO: add reputation points to both seller and buyer
        _addreputationPoints(seller, winnerBidder, bidPrice);

        // finish listing
        _finalizeListing(listingId, winnerBidder, ListingStatus.Sold);
        // if bid time is less than 15 min, increase by 15 min
        // retuen bid id
        emit FullfillBid(
            bidToListing[listingId].bidId,
            listingId,
            _NFTContract,
            winnerBidder,
            tokenId,
            bidPrice,
            issuer,
            royaltyAmount,
            fees,
            netPrice,
            block.timestamp
        );
    }

    // delist
    /**
     * @dev called by seller through dapps when s/he wants to remove this token from the marketplace
     * @notice auction can't be canceled , if seller delist time on sale on maretplace before time to delist, he will pay a fine
     * @param listingId listing id
     * @return _NFTContract nft contract address
     * @return tokenId token id
     */
    function deList(bytes32 listingId) external returns (address _NFTContract, uint256 tokenId) {
        ListingStatus status = _tokenListings[listingId].status;
        address buyer = _tokenListings[listingId].buyer;
        address owner = _tokenListings[listingId].seller;
        _NFTContract = _tokenListings[listingId].nFTContract;
        uint256 releaseTime = _tokenListings[listingId].releaseTime;
        uint256 listingPrice = _tokenListings[listingId].listingPrice;
        tokenId = _tokenListings[listingId].tokenId;
        require(owner == _msgSender(), 'Caller is not the owner');
        require(buyer == address(0), 'Already bought token');
        uint256 timeToDelistAuction = _calcSum(releaseTime, 3 days);

        // require(status==ListingStatus.OnMarket || status==ListingStatus.onAuction,"Already bought or canceled token");
        require(
            (timeToDelistAuction <= block.timestamp && status == ListingStatus.onAuction) ||
                (status == ListingStatus.OnMarket),
            "Can't delist"
        );
        uint256 fineAmount;
        uint256 remaining;
        // if realse time < now , pay
        if (status != ListingStatus.onAuction) {
            if (releaseTime < block.timestamp) {
                // if it's not auction ? pay,
                (fineAmount, remaining) = _getDeListingQualAmount(listingPrice);
                //TODO: deduct the fine from his stake contract

                require(_deduct(owner, getAdminWallet(), fineAmount), "couldn't deduct the fine");
            } else {
                remaining = _getListingQualAmount(listingPrice);
            }
            // update user reserves
            // reserve nigative couldn't be at any case
            require(_updateUserReserves(_msgSender(), remaining, false) >= 0, 'negative reserve is not allowed');
        }

        // trnasfer token
        require(_safeNFTTransfer(_NFTContract, tokenId, address(this), owner), "NFT token couldn't be transfered");
        // finish listing
        _finalizeListing(listingId, address(0), ListingStatus.Canceled);
        emit DeListOffMarketplace(
            listingId,
            _NFTContract,
            owner,
            tokenId,
            fineAmount,
            remaining,
            releaseTime,
            block.timestamp
        );
        // if bid time is less than 15 min, increase by 15 min
        // retuen bid id
    }

    // buynow
    /**
     * @dev called by buyer through dapps when s/he wants to buy a gevin NFT  token from the marketplace
     * @notice  if auction, the seller must enabe forSale. prices should be more than or equal the listing price
     * @param listingId listing id
     * @param price gevin price
     * @return _NFTContract nft contract address
     * @return tokenId token id
     */
    function buyNow(bytes32 listingId, uint256 price) external returns (address _NFTContract, uint256 tokenId) {
        bool sellForEnabled = _tokenListings[listingId].sellForEnabled;
        address seller = _tokenListings[listingId].seller;
        _NFTContract = _tokenListings[listingId].nFTContract;
        tokenId = _tokenListings[listingId].tokenId;
        require(price >= _tokenListings[listingId].listingPrice, 'Invalid price');
        require(
            _tokenListings[listingId].status == ListingStatus.OnMarket ||
                (_tokenListings[listingId].status == ListingStatus.onAuction &&
                    sellForEnabled == true &&
                    _tokenListings[listingId].releaseTime > block.timestamp),
            'Token isnot for sale '
        );
        // check that contract is allowed to transfer tokens
        require(
            _getAllowance(_msgSender()) >= price,
            'Marketplace is not allowed to withdraw the required amount of tokens'
        );
        // transfer price

        (address issuer, uint256 royaltyAmount, uint256 fees, uint256 netPrice) = _getListingFinancialInfo(
            _NFTContract,
            tokenId,
            price
        );

        require(_safeTokenTransferFrom(_msgSender(), owner(), fees), "Couldn't transfer token as fees");
        if (issuer != address(0)) {
            require(_safeTokenTransferFrom(_msgSender(), issuer, royaltyAmount), "Couldn't transfer token to issuer");
        }

        // token value could be zero ater taking the roylty share ??? need to ask?
        require(_safeTokenTransferFrom(_msgSender(), seller, netPrice), "Couldn't transfer token to seller");
        // trnasfer token
        require(
            _safeNFTTransfer(_NFTContract, tokenId, address(this), _msgSender()),
            "NFT token couldn't be transfered"
        );
        uint256 ListingQualAmount = _getListingQualAmount(_tokenListings[listingId].listingPrice);

        require(_updateUserReserves(seller, ListingQualAmount, false) >= 0, 'negative reserve is not allowed');

        // finish listing
        _finalizeListing(listingId, _msgSender(), ListingStatus.Sold);
        // TODO: add reputation points to both seller and buyer
        _addreputationPoints(seller, _msgSender(), price);
        emit BuyNow(
            listingId,
            _NFTContract,
            _msgSender(),
            tokenId,
            price,
            seller,
            sellForEnabled,
            issuer,
            royaltyAmount,
            fees,
            netPrice,
            block.timestamp
        );
        // if bid time is less than 15 min, increase by 15 min
        // retuen bid id
    }

    /**
     * @dev called by seller through dapps when his/her auction is  not fullfilled after 3 days
     *  @notice  after auction with winner bid . bidder didn't call fullfile within 3 days of auction closing  auction owner can call dispute to delist and punish the spam winner bidder fine is share between the plateform and the auction owner
     * @param listingId listing id
     * @return _NFTContract nft contract address
     * @return tokenId token id
     */
    function disputeAuction(bytes32 listingId) external returns (address _NFTContract, uint256 tokenId) {
        address winnerBidder = bidToListing[listingId].bidder;
        address seller = _tokenListings[listingId].seller;
        _NFTContract = _tokenListings[listingId].nFTContract;
        tokenId = _tokenListings[listingId].tokenId;
        uint256 qualifyAmount = _tokenListings[listingId].qualifyAmount;
        uint256 timeToDispute = _calcSum(_tokenListings[listingId].releaseTime, 3 days);
        require(winnerBidder != address(0) && timeToDispute >= block.timestamp, 'No bids or still running auction');
        require(seller == _msgSender(), 'Caller is not the owner');
        require(!listingBids[listingId][winnerBidder].isPurchased, 'Already purchased');
        // call staking contract to deduct
        (uint256 fineAmount, uint256 remaining) = _calcBidDisputeFees(qualifyAmount);
        require(_deduct(winnerBidder, getAdminWallet(), fineAmount), "couldn't deduct the fine for the admin wallet");
        require(_deduct(winnerBidder, seller, remaining), "couldn't deduct the fine for the admin wallet");
        // trnasfer token
        require(_safeNFTTransfer(_NFTContract, tokenId, address(this), seller), "NFT token couldn't be transfered");
        require(_updateUserReserves(winnerBidder, qualifyAmount, false) >= 0, 'negative reserve is not allowed');

        // finish listing
        _finalizeListing(listingId, address(0), ListingStatus.Canceled);
        // if bid time is less than 15 min, increase by 15 min
        // retuen bid id
        emit DisputeAuction(
            bidToListing[listingId].bidId,
            listingId,
            _NFTContract,
            winnerBidder,
            tokenId,
            seller,
            qualifyAmount,
            remaining,
            fineAmount,
            block.timestamp
        );
    }

    /**
    * @dev called by user through dapps when his/her wants to free his reserved tokens which are no longer in active auction or listing
    *  @notice this function is greedy, called by user only when s/he wants rather than force the check & updates with every transaction which might be very costly .

    * @return curentReserves user reserves after freeing the unused reservd

     */

    function freeReserves() external returns (uint256 curentReserves) {
        // TODo: Check allternative for gas consumptions
        // iterate over the listng key map
        // if it's sold, canceled,  free if he is participating on this listing
        uint256 lastReserves = userReserves[_msgSender()];
        bytes32[] memory listings = userListing[_msgSender()];
        delete userListing[_msgSender()];
        bytes32[] storage newListings = userListing[_msgSender()];

        // loop
        for (uint256 index = 0; index < listings.length; index++) {
            if (_tokenListings[listings[index]].status == ListingStatus.onAuction) {
                newListings.push(listings[index]);
                curentReserves = _calcSum(curentReserves, _tokenListings[listings[index]].qualifyAmount);
            } else if (_tokenListings[listings[index]].status == ListingStatus.OnMarket) {
                newListings.push(listings[index]);
                uint256 listQualifyAmount = _getListingQualAmount(_tokenListings[listings[index]].listingPrice);

                curentReserves = _calcSum(curentReserves, listQualifyAmount);
            }
        }
        userListing[_msgSender()] = newListings;
        require(_setUserReserves(_msgSender(), curentReserves), 'set reserve faild');
        emit UserReservesFree(_msgSender(), lastReserves, curentReserves, block.timestamp);
    }

    // ubnormal isssue with calling owner() in deList unction , we have implemented this func as a workaround
    function getAdminWallet() private view returns (address) {
        return owner();
    }
}
","keccak256":"0xf6f387ed54208044f87fe04c19d54c77834e3e23cb73dd5bda04676e00a8e3a5","license":"AGPL-3.0-or-later"},"contracts/StartfiMarketPlaceFinance.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0-or-later

pragma solidity 0.8.7;
pragma abicoder v2;
import './interface/IStartFiReputation.sol';

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import './interface/IStartFiStakes.sol';
import './MarketPlaceBase.sol';

/**
 * @author Eman Herawy, StartFi Team
 *desc   contract to handle all financial work for the marketplace
 * @title Startfi Marketplace Finance
 */
contract StartfiMarketPlaceFinance is MarketPlaceBase {
    /******************************************* decalrations go here ********************************************************* */
    address internal _paymentToken;
    uint256 internal _feeFraction = 1;
    uint256 internal _feeBase = 100;
    uint256 bidPenaltyPercentage = 1;
    uint256 public delistFeesPercentage = 1;
    uint256 public listqualifyPercentage = 1;
    uint256 public bidPenaltyPercentageBase = 100;
    uint256 public delistFeesPercentageBase = 100;
    uint256 public listqualifyPercentageBase = 100;
    mapping(address => uint256) userReserves;
    mapping(address => bytes32[]) userListing;
    address public stakeContract;
    address reputationContract;

    /******************************************* constructor goes here ********************************************************* */

    constructor(
        string memory _name,
        address _paymentContract,
        address _reputationContract
    ) MarketPlaceBase(_name) {
        _paymentToken = _paymentContract;
        reputationContract = _reputationContract;
    }

    /******************************************* modifiers go here ********************************************************* */

    /******************************************* read state functions go here ********************************************************* */

    function _calcSum(uint256 a, uint256 b) internal pure returns (uint256 result) {
        result = a + b;
    }

    /**
     @dev calculat the platform fees
    *@param price  : item  price
    *@return fees the value that the platform will get
     */
    function _calcFees(uint256 price) internal view returns (uint256 fees) {
        fees = (price * _feeFraction) / _feeBase;
    }

    /**
     @dev calculat the platform fine amount when seller delist before time
    *@param listingPrice  : item listing price
    *@return amount the value that the platform will get
     */
    function _getListingQualAmount(uint256 listingPrice) internal view returns (uint256 amount) {
        amount = (listingPrice * listqualifyPercentage) / listqualifyPercentageBase;
    }

    /**
     @dev calculat the platform fine amount when seller delist before time
    *@param listingPrice  : item listing price
    *@return fineAmount the value that the platform will get
    *@return remaining the value remaing after subtracting the fine
     */
    function _getDeListingQualAmount(uint256 listingPrice)
        internal
        view
        returns (uint256 fineAmount, uint256 remaining)
    {
        fineAmount = (listingPrice * delistFeesPercentage) / delistFeesPercentageBase;
        remaining = _getListingQualAmount(listingPrice) - fineAmount;
    }

    /**
      @dev calculat the platform share when seller call disput
    *@param qualifyAmount  : seller defind value to be staked in order to participate in a gevin auction
    * @return fineAmount the value that the platform will get
    * @return remaining the value that the auction woner will get
     */
    function _calcBidDisputeFees(uint256 qualifyAmount) internal view returns (uint256 fineAmount, uint256 remaining) {
        fineAmount = (qualifyAmount * bidPenaltyPercentage) / bidPenaltyPercentageBase;
        remaining = qualifyAmount - fineAmount;
    }

    function _getListingFinancialInfo(
        address _NFTContract,
        uint256 tokenId,
        uint256 bidPrice
    )
        internal
        view
        returns (
            address issuer,
            uint256 royaltyAmount,
            uint256 fees,
            uint256 netPrice
        )
    {
        fees = _calcFees(bidPrice);
        netPrice = bidPrice - fees;
        // royalty check
        if (_supportRoyalty(_NFTContract)) {
            (issuer, royaltyAmount) = _getRoyaltyInfo(_NFTContract, tokenId, bidPrice);
            if (royaltyAmount > 0 && issuer != address(0)) {
                netPrice = netPrice - royaltyAmount;
            }
        }
    }

    /**
     *@param user  : participant address
     * @return the value of user reserves
     */
    function getUserReserved(address user) external view returns (uint256) {
        return userReserves[user];
    }

    /**
     *
     * @return the value of the state variable `_feeFraction`
     */
    function getServiceFee() external view returns (uint256) {
        return _feeFraction;
    }

    /**
     * @dev :wrap function to get the total allowed number of tokens that this contract can transfer from the given account 

    * @param owner: owner address
    * @return allowed number of tokens that this contract can transfer from the owner account
     */
    function _getAllowance(address owner) internal view returns (uint256) {
        return IERC20(_paymentToken).allowance(owner, address(this));
    }

    /**
     * @dev this function calls StartFiStakes contract to get the total staked tokens for 'user' an substract the current reserves to get the total number of free tokens
     * @param staker : participant address
     * @return allowed number of tokens that this contract can transfer from the owner account
     */
    function _getStakeAllowance(
        address staker /*,uint256 prevAmount*/
    ) internal view returns (uint256) {
        // user can bid multi time, we want to make sure we don't calc the old bid as sperated bid
        uint256 userActualReserved = userReserves[staker]; //.sub(prevAmount);
        return IStartFiStakes(stakeContract).getReserves(staker) - userActualReserved;
    }

    /******************************************* state functions go here ********************************************************* */
    /**
     * @notice  all conditions and checks are made prior to this function
     * @dev this function calls StartFiStakes contract to subtract the user stakes and add that value to the 'to'
     * @param finePayer : fine payer address
     * @param to : participant address
     * @param amount : value to be deducted from his stakes as a fine
     * @return true if it's done
     */
    function _deduct(
        address finePayer,
        address to,
        uint256 amount
    ) internal returns (bool) {
        return IStartFiStakes(stakeContract).deduct(finePayer, to, amount);
    }

    /**
     * @notice  all conditions and checks are made prior to this function. math of point calcualtion is not done yet
     * @dev this function calls StartFiReputation contract to mint reputation points for both seller and buyer
     * @param seller : seller address
     * @param buyer : buyer address
     * @param amount : price
     * @return buyerBalance : buyer current reputation balance
     * @return sellerBalance : seller current reputation balance
     */
    function _addreputationPoints(
        address seller,
        address buyer,
        uint256 amount
    ) internal returns (uint256 buyerBalance, uint256 sellerBalance) {
        // calc how much pint for both of them ??
        // TODO: math and logic for calc the point based on the amount
        uint256 sellerPoints = amount / 2;
        uint256 buyerPoints = amount / 2;
        sellerBalance = IStartFiReputation(reputationContract).mintReputation(seller, sellerPoints);
        buyerBalance = IStartFiReputation(reputationContract).mintReputation(buyer, buyerPoints);
    }

    function _safeTokenTransfer(address to, uint256 amount) internal returns (bool) {
        return IERC20(_paymentToken).transfer(to, amount);
    }

    /**
     * @dev  Safely transfers `amount` of token from `from` to `to`.
     * @param from address representing the previous owner of the token
     * @param to target address that will receive the tokens
     * @param amount number of tokens to be transferred
     * See {transferFrom}
     */
    function _safeTokenTransferFrom(
        address from,
        address to,
        uint256 amount
    ) internal returns (bool) {
        return IERC20(_paymentToken).transferFrom(from, to, amount);
    }

    /**
     * @notice  all conditions and checks are made prior to this function
     * @dev called to set user reserves
     * @param user : participant address
     * @param newReservedValue : value to be sored as user reserve
     */
    function _setUserReserves(address user, uint256 newReservedValue) internal returns (bool) {
        userReserves[user] = newReservedValue;
        return true;
    }

    /**
     * @notice  all conditions and checks are made prior to this function
     * @dev called to increase or decrease user reserves
     * @param user : participant address
     * @param newReserves : value to be added or substracted
     * @param isAddition : true if we are adding the new value
     */
    function _updateUserReserves(
        address user,
        uint256 newReserves,
        bool isAddition
    ) internal returns (uint256 _userReserves) {
        _userReserves = isAddition ? userReserves[user] + newReserves : userReserves[user] - newReserves;
        userReserves[user] = _userReserves;
        return _userReserves;
    }

    /**
     *   * @notice  all conditions and checks are made prior to this function
     * @dev  the formula is (fees * 1000)/base
     * @param newFees  the new fees value to be stored
     * @param newBase  the new basefees value to be stored
     * @return percentage the value of the state variable `_feeFraction`
     */
    function changeFees(uint256 newFees, uint256 newBase) internal returns (uint256 percentage) {
        require(newFees <= newBase, 'Fee fraction exceeded base.');
        percentage = (newFees * 1000) / newBase;
        require(percentage <= 30 && percentage >= 10, 'Percentage should be from 1-3 %');

        _feeFraction = newFees;
        _feeBase = newBase;
    }

